<script lang="ts">
import Vue from 'vue'
import axios from 'axios'

declare global {
  interface Window {
    $crisp: any
  }
}

const run = async (config: any, name: string, data: any) => {
  const options = {
    headers: {
      'Content-Type': 'application/json',
      'x-bnb-apikey': config.APIKEY,
    },
  }
  const response = await axios.post(`${config.BASEAPI}/${name}`, data, options)
  return response.data
}

export default Vue.extend({
  data() {
    return {
      email: '',
      bnbcode: '',
      firstname: '',
      mode: {
        contact: false,
        error: false,
        loading: false,
      },
    }
  },
  methods: {
    toHost() {
      this.$router.push(`/`)
    },
    showContact() {
      this.mode.loading = true
      console.log('this.email', this.email)
      console.log('this.firstname', this.firstname)

      window.$crisp.push(['set', 'user:email', [this.email]])
      window.$crisp.push(['set', 'user:nickname', [this.firstname]])
      run(this.$config, 'getBnbContact', {
        email: this.email,
        bnbcode: this.bnbcode,
        firstname: this.firstname,
      }).then((data) => {
        this.mode.loading = false
        if (data.error) {
          console.log(data)
          this.mode.error = true
          window.$crisp.push(['do', 'chat:open'])
        }
        this.mode.contact = true
      })
    },
    contactUs() {
      window.location.href = 'mailto:hello@bnb.contact'
    },
  },
})
</script>

